window.addEventListener("load",t=>new Main);class Main{constructor(){this.letters=document.querySelectorAll("#hello path"),this.speed=2,this.colorsIndex=0,this.bgColorsIndex=0,this.colors=["#C30807","#11D22C","#112BD2"],this.bgColors=["#07C2C3","#D211B7","#D2B811"],this.lettersData=[{letter:"h",x:0,y:0,duration:1800,delay:0},{letter:"e",x:132,y:80,duration:600,delay:1800},{letter:"l",x:180,y:15,duration:600,delay:1200},{letter:"l",x:222,y:15,duration:600,delay:1e3},{letter:"o",x:255,y:90,duration:600,delay:900},{letter:"w",x:150,y:200,duration:1800,delay:900},{letter:"o",x:300,y:245,duration:600,delay:1050},{letter:"r",x:345,y:250,duration:1e3,delay:1e3},{letter:"l",x:440,y:184,duration:600,delay:1e3},{letter:"d",x:480,y:178,duration:1e3,delay:1e3},{letter:"!",x:600,y:190,duration:600,delay:1e3},{letter:".",x:580,y:290,duration:600,delay:1e3},{letter:" ",x:580,y:290,duration:600,delay:1e3}],this.setLetters(),this.start()}setLetters(){for(let t=0;t<this.lettersData.length;t++)this.letters[t].setAttribute("transform",`translate(${this.lettersData[t].x},${this.lettersData[t].y})`)}start(){anime({targets:"#hello path",loop:!0,direction:"alternate",strokeDashoffset:[anime.setDashoffset,0],easing:"linear",duration:(t,e)=>this.lettersData[e].duration/this.speed,delay:(t,e)=>e*this.lettersData[e].delay/this.speed,update:t=>{0===t.progress&&(this.colorsIndex=(this.colorsIndex+1)%this.colors.length,this.bgColorsIndex=(this.bgColorsIndex+1)%this.bgColors.length,this.letters.forEach(t=>{t.style.stroke=this.colors[this.colorsIndex]}),document.body.style.backgroundColor=this.bgColors[this.bgColorsIndex])}})}}
